var link = document.querySelector(".adress-btn"),
    popup = document.querySelector(".modal"),
    close = document.querySelector(".modal-close"),
    form = popup.querySelector("form"),
    username = popup.querySelector("[name=username]"),
    email = popup.querySelector("[name=email]"),
    storage = localStorage.getItem("username");
link.addEventListener("click", function(e) { e.preventDefault(), popup.classList.add("modal-show"), storage ? (username.value = storage, email.focus()) : username.focus() }), close.addEventListener("click", function(e) { e.preventDefault(), popup.classList.remove("modal-show") }), form.addEventListener("submit", function(e) { username.value && email.value ? localStorage.setItem("username", username.value) : (e.preventDefault(), alert("Введите имя и еmail")) });